module.exports = {
    Cluster: function(name){
        this.name  = name;
        this.features = [];
        this.src=[];
        this.target=[];
        this.targetActivity=[];
        this.x;
        this.y;

        this.addFeature = function(feature){
            this.features.push(feature);
        };
        this.addTarget=function(clusterTarget) {
            this.target.push(clusterTarget.name);
            clusterTarget.addSrc(this);
        }
        this.addSrc=function(clusterSrc) {
            this.src.push(clusterSrc.name);
        }
        this.addActivity=function(activity) {
            this.targetActivity.push(activity);
        }
    }
}
