module.exports = {
    Cluster: function(name){
        this.name  = name;
        this.features = [];
        this.state;
        this.size;
        this.src=[];
        this.target=[];
        this.targetActivity=[];
        this.kernel=[];
        this.matching=[];
        this.x;
        this.y;

        this.addSize=function(size) {
            this.size=size;
        };
        this.addFeature = function(feature){
            this.features.push(feature);
        };
        this.addKernel = function(feature, clusterTarget){
            this.kernel.push(feature);
            clusterTarget.kernel.push(feature);
        };
        this.addTarget=function(clusterTarget) {
            this.target.push(clusterTarget.name);
            clusterTarget.addSrc(this);
        }
        this.addSrc=function(clusterSrc) {
            this.src.push(clusterSrc.name);
        }
        this.addActivity=function(activity) {
            this.targetActivity.push(activity);
        }
        this.addStateVanished=function() {
            this.state="vanished";
        }
        this.addStateAppeared=function() {
            this.state="appeared";
        }
        this.addMatchingType=function(matchingType) {
            this.matching.push(matchingType);
        }
    }
}
