var logger=require('logger');
var express=require('express');
var app =express();
var resultats=Object();
to_render='generic_ejs.ejs'

module.exports= {
	barchart : function(res, result) {
		if ("page" in result) {
			res.render(to_render, {objectResult: result, page: result.page});
		}
		else {
			res.render(to_render, {objectResult: result, page:'barchart'});
		}
		
	},
	marimekko : function(res, result) {
		res.render(to_render, {objectResult: result, page:'marimekko'});
	},
	embeddedCircle : function(res, result) {
		res.render(to_render, {objectResult: result, page:'embeddedCircle'});
	},
	timeline : function(res, result) {
		res.render(to_render, {objectResult: result, page:'timeline'});
	},
	diachronie : function(res, result) {
		res.render(to_render, {objectResult: result, page:'bipartite'});
	}	,
	sankey : function(res, result) {
		res.render(to_render, {objectResult: result, page:'sankey'});
	}
}